import React, { Component } from "react";
import Countdown from "./Countdown";

class Clock extends Component {
  constructor(props) {
    super(props);
    this.state = { deadline: "January, 10, 2030" };
  }
  render() {
    return (
      <div className="Clock">
        <div className="Clock-title">Countdown Timer</div>
        <div className="Clock-date">{this.state.deadline}</div>
        <Countdown deadline={this.state.deadline} />
      </div>
    );
  }
}
export default Clock;
