import React, { Component } from 'react';
import {
  Link,
} from "react-router-dom";
import "./App.scss";

let dialogStyles = {
};

let dialogCloseButtonStyles = {
    marginBottom: '0px',
    padding: '3px 8px',
    cursor: 'pointer',
    borderRadius: '50%',
    border: 'none',
    width: '30px',
    height: '30px',
    fontWeight: 'bold',
    alignSelf: 'flex-end',
    backgroundColor: '#ffffff',
    fontFamily: 'Montserrat',
    fontSize: '20px'
};


class Dialog extends Component {
    render() {
    	const mobileNavigation = [
		    {
	          cta: 'Our Story',
	          url: '/ourstory'
	        },
	        {
	          cta: 'RSVP',
	          url: 'rsvp'
	        },
	        {
	          cta: 'Moments',
	          url: '/moments'
	        },
	        {
	          cta: 'Registry',
	          url: '/registry'
	        },
		]

        let dialog = (
            <div className="navbar-overlay"  id="dialog-navigation-styles" style={dialogStyles}>
                <button className="overlay-close-button" onClick={this.props.onClose}>x</button>

                <h1 className="header">Frederick &amp; Grace</h1>

				<ul className="navbar-nav">
		            <li className="nav-item" key="Welcome">
		              <Link className="nav-link" to='/' onClick={this.props.onClose}>Welcome</Link>
		            </li>

		          { mobileNavigation.map((nav) => (
		            <li className="nav-item" key={nav.cta}>
		              <Link className="nav-link" to={nav.url} onClick={this.props.onClose}>{nav.cta}</Link>
		            </li>
		          ))}

		        </ul>
            </div>
        );

        if (! this.props.overlayIsOpen) {
            dialog = null;
        }

        return (
            <div className="overlay-wrapper">
                {dialog}
            </div>
        );
    }
}

export default Dialog;
